  const played = history[move];
  function Botton({ value, Click }) {
    return (
      <button className="st table" onClick={Click}>
        {value}
      </button>
    );
  }
  const handleClick = (i = false, onPlayerClick, player) => {
    const clickplayer = player.slice();
    if (isChange) {
      clickplayer[i] = "X";
      setIsChange(false);
    } else {
      clickplayer[i] = "O";
    }
    onPlayerClick(clickplayer);
  };

  const Click = (frist, player, onPlayerClick) => {
    return (
      <>
        <div className="">
          <Botton
            value={player[0]}
            Click={() => handleClick(0, frist, onPlayerClick, player)}
          />
          <Botton value={player[1]} Click={() => handleClick(1)} />
          <Botton value={player[2]} Click={() => handleClick(2)} />
        </div>
        <div className="">
          <Botton value={player[3]} Click={() => handleClick(3)} />
          <Botton value={player[4]} Click={() => handleClick(4)} />
          <Botton value={player[5]} Click={() => handleClick(5)} />
        </div>
        <div className="">
          <Botton value={player[6]} Click={() => handleClick(6)} />
          <Botton value={player[7]} Click={() => handleClick(7)} />
          <Botton value={player[8]} Click={() => handleClick(8)} />
        </div>
      </>
    );
  };

  return (
    <div className="App">
      <p>X/O Game</p>
      <Click frist={frist} player={played} onPlayerClick={""} />
    </div>
  );
}

export default App;
