import "./App.css";

function App() {
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [move, setMove] = useState(0);
  const frist = move % 2 === 0;
  const played = history[move];
  function Botton({ value, Click }) {
    return (
      <button className="st table" onClick={Click}>
        {value}
      </button>
    );
