      clickplayer[i] = "X";
    } else {
      clickplayer[i] = "O";
    }
    onPlayerClick(clickplayer);
  };

  function Click(frist, player, onPlayerClick) {
    handleClick(frist);
    return (
      <>
        <div className="board-row">
          <Botton value={player[0]} Click={() => handleClick(0)} />
